import React,{useState} from 'react';
import Default_page from './Default_page';
import Editor from './Editor';
import View from './View';
import NotesCard from './NotesCard';
function App() {
  const[notes,setnotes]=useState([])
  return (
    <> <BrowserRouter>
    <Routes>
      <Route path="/notes/{id}" element={<View />}></Route>
      <Route path="/notes/{id}/edit" element={<Editor />}></Route>
    </Routes>
  </BrowserRouter>
  {/* <> */}

  <div id="entire_header">
  <button id="nav">&#9776;</button>
  <div id="title_and_subtitle">
    <h1>Lotion</h1> <h2>Like Notion but worse</h2>
  </div>
</div>
<div id="bottom_section">
    <div id="left_bar">
      <div id="Add_notes">
        <div className="notes">
          <div id="FirstBoxNotes">Notes</div>
          <button id="new_note" >&#43;</button>





    </div>
      <NotesCard/>
        {/* the line above is to see what notes list outputs when value is passed in */}
      </div>
    </div>
    <Default_page/>
    {/* outlet goes here */}
</div>)



</>

  );
}
